import React, { useState } from 'react';
import { Input, Button, Form } from 'antd';
import axios from 'axios';

function QuestionPanel({ chapterId }) {
  const [question, setQuestion] = useState('');
  const [answer, setAnswer] = useState('');
  const API_URL = process.env.REACT_APP_API_URL;

  function handleSubmit() {
    axios.post('/api/question', { chapterId: chapterId, question: question }).then(function (response) {
      setAnswer(response.data.answer);
    });
  }

  return React.createElement(
    'div',
    null,
    React.createElement(
      Form,
      null,
      React.createElement(
        Form.Item,
        null,
        React.createElement(Input.TextArea, {
          placeholder: 'Enter your question',
          value: question,
          onChange: function (e) { setQuestion(e.target.value); },
        })
      ),
      React.createElement(
        Button,
        { type: 'primary', onClick: handleSubmit },
        'Search'
      )
    ),
    React.createElement(
      'div',
      { style: { marginTop: '20px' } },
      React.createElement('h3', null, 'Answer:'),
      React.createElement('p', null, answer)
    )
  );
}

export default QuestionPanel;
